$(document).ready(function(){$("#book-table").DataTable({processing:!0,ajax:{url:"/api/book/",dataSrc:""},columns:[{data:"rowKey"},{data:"jacket"},{data:"title"},{data:"series"},{data:"author"},{data:"price"}],columnDefs:[{targets:0,data:"ISBN",render:function(n,t,i){return'<a href="https://www.edelweiss.plus/#sku='+i.rowKey+'" target="_blank">'+i.rowKey+"<\/a>"}},{targets:1,data:"jacket",render:function(n,t,i){return'<img src="'+i.jacketUrl+'?width=50"/>'}},{targets:5,data:"price",render:function(n,t,i){return"$"+i.price}}]})});