$(document).ready(function(){$("#book-table").DataTable({processing:!0,ajax:{url:"/api/book/",dataSrc:""},columns:[{data:"rowKey"},{data:"jacket"},{data:"title"},{data:"series"},{data:"author"},{data:"price"}],columnDefs:[{targets:0,render:function(n,t,i){return'<a href="https://www.edelweiss.plus/#sku='+i.rowKey+'" target="_blank">'+i.rowKey+"<\/a>"}},{targets:1,className:"dt-body-center"},{targets:1,render:function(n,t,i){return'<img src="'+i.jacketUrl+'?width=50" title="'+i.title+'"/>'}},{targets:5,className:"dt-body-right",render:function(n,t,i){if(!i.price)return"";i.price+="";x=i.price.split(".");x1=x[0];x2=x.length>1?"."+x[1]:".00";for(var r=/(\d+)(\d{3})/;r.test(x1);)x1=x1.replace(r,"$1,$2");return"$"+x1+x2}}]})});